import React, { useState,useRef } from 'react';

function TodoForm(props) {
    const [todo,settodo]=useState('');
    const textInput = useRef();

    function handleChange(e){
        settodo(e.target.value);
    }

    function addToList(e){
        e.preventDefault();
        if(textInput.current.value.trim()!=='')
        {
        props.onAdd(todo);
        settodo('');
        textInput.current.focus();
        }
        else{
            textInput.current.focus();
        }
    }


  return (
    <div>
         <div className="row">
             <div className='col-xs-7'>
        <form onSubmit={addToList}> 
            <input className='' type='text' ref={textInput} value={todo} onChange={handleChange}></input>
            <button className='btn btn-primary' type='submit'>Add</button>        
        </form>
        </div>
        <div className="col-xs-5">
                        <ul className="nav nav-pills">
                            <li><button className={`btn ${isActive ?'btn-primary':'deafult'}`} value="SHOW_ALL">All</button></li>
                            <li><button value="false">Incomplete</button></li>
                             <li><button value="true">Completed</button></li>
                        </ul>
                    </div>
        </div>
    </div>
  );
}

export default TodoForm;
