import React,{useRef, useState} from 'react';

function TodoList(props) {
    const [input,changeInput]=useState('');
    const textInput=useRef();

    function changeEditMode(i){
        props.changeEditMode(i);
    }

    function removeItem(i){
        props.removeItem(i);
    }

    function handleChange(e){
        changeInput(e.target.value);
    }
    function updateIteminState(i){

        const itemInput =textInput.current.value;
        props.updateIteminState(itemInput,i);
    }

  return (
    <div>
        <ul>
             {Object.keys(props.list).map((item,index) => {
                    return (
                        <li key={index} > 
                                <div>
                                {
                                props.list[index].isEditable ?
                                <div>
                                    <input 
                                        style={{float:'left'}} 
                                        value={props.list[index].item} 
                                        onChange={handleChange} 
                                        ref={textInput}></input>
                                    <button style={{float:'left'}} onClick={()=>changeEditMode(index)}>X</button>
                                    <button style={{float:'left'}} onClick={()=>updateIteminState(index)}>OK</button>
                                </div>:
                                <div>
                                    <div style={{float:'left'}} onClick={()=>changeEditMode(index)}>{props.list[index].item}</div>
                                    <button type='button' className="btn btn-danger " onClick={()=>removeItem(index)}>Delete</button>
                                </div>
                                }
                                </div>
                        </li>  
                )})}
        </ul>
    </div>
  );
}

export default TodoList;
