import React, { useState,useRef } from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

function TodoForm(props) {
    const [todo,settodo]=useState('');
    const textInput = useRef();

    function handleChange(e){
        e.target.name==='filterResult' ? props.passResults(e.target.value): settodo(e.target.value);
    }

    function addToList(e){
        e.preventDefault();
        if(textInput.current.value.trim()!==''){
            props.onAdd(todo);
            settodo('');
            textInput.current.focus();
        }
        else{
            textInput.current.focus();
        }
    }

    function showResults(value){
        return (props.filter[0]===value ?'btn-primary':'btn-deafult');
    }

  return (
    <div>
         <div className="row">
             <div className='col-md-4'>
            <form className='form-group form-control' onSubmit={addToList}> 
            <TextField className='input' type='text' ref={textInput} value={todo} onChange={handleChange}></TextField>
            <Button type='submit'>Add</Button>        
        </form>
        </div>
        <div className="col-md-4">
            <ul className="nav nav-pills">              
                <li><button name='filterResult' onClick={handleChange} className={`btn ${showResults('all')}`} value='all'>All</button></li>
                <li><button name='filterResult' onClick={handleChange} className={`btn ${showResults('false')}`} value="false">Pending</button></li>
                <li><button name='filterResult' onClick={handleChange} className={`btn ${showResults('true')}`}value="true">Done</button></li>

                        </ul>
                    </div>
        </div>
    </div>
  );
}

export default TodoForm;
