import React from 'react';

function TodoList(props) {

    function removeItem(index){
      props.removeItem(index);
    }
    function editItem(e){
     props.editItem(e.target.value);
    }

  return (
    <div>
        <table>
            <tbody>
                {props.list.map((item,index) => {
                    return (
                        <tr>
                            <td key={index} value={index} style={{float:'left'}}> 
                                {item}
                                <button type='button' value={item} className="btn btn-secondary pull-right" onClick={editItem}>Edit</button>
                                <button type='button' value={index} className="btn btn-danger pull-right" onClick={removeItem(index)}>Delete</button>
                            </td>
                        </tr>  
                )})}
            </tbody>
        </table>
    </div>
  );
}

export default TodoList;
