import React, { useState } from 'react';
import './App.css';
import Header from './Header';
import TodoForm from './Todo/TodoForm';
import TodoList from './Todo/TodoList';


function App() {
  const [state,setState]=useState({items:[]});

  function addItemToState(workItem){
    console.log(state.items);
    let item = {item:workItem,isComplete:false,isEditable:false};
    /* setState(
      ...state, {items:
        [...state.items,item]}
      );  */
    let newState = JSON.parse(JSON.stringify(state));  
    setState([{...newState.items,item}]);

  }

  function removeItemFromList(index){
    let newState = JSON.parse(JSON.stringify(state));
    newState.items.splice(index, 1); 
    setState(newState);
  }

  function changeEditModeinList(index){
    let newState = JSON.parse(JSON.stringify(state));
    newState.items[index].isEditable = !state[index].isEditable;
    setState(newState);
  }

  function changeIsDone(check, index){
    console.log(check+'     '+index);
    let newState = JSON.parse(JSON.stringify(state));
    newState.items[index].isComplete = check;
    setState(newState);

  }

  function updateIteminState(item,index){
    let newState = JSON.parse(JSON.stringify(state));
    newState.items[index].item = item;
    newState.items[index].isEditable=false;
    setState(newState);
  }

  return (
    <div className="App">
      <Header/>
      <TodoForm onAdd={addItemToState}/>
      <TodoList list={state} 
       /*  removeItem={removeItemFromList} 
        changeDoneStatus ={changeIsDone}
        changeEditMode={changeEditModeinList} 
        updateIteminState={updateIteminState} *//>
    </div>
  );
}

export default App;
