    function handleChange(e){
       setlist(e.target.value);
    }

    function addToList(e){
        e.preventDefault();
        console.log('yes');
        if(sideInput.current.value.trim()!==''){
            props.onAdd(sideList);
            settodo('');
            sideInput.current.focus();
        }
        else{
            sideInput.current.focus();
        }
    }


  return (
    <div>
         <div className="row">
                <form onSubmit={addToList}> 
                    <input className='sidebarInput' type='text' ref={sideInput} value={sideList} onChange={handleChange}></input>
                    <button className='btn btn-primary' type='submit'>Add</button>        
                </form>
        </div>
    </div>
  );
}

export default SidebarForm;
