import React, { useState } from 'react';
import './App.css';
import Header from './Header';
import TodoForm from './Todo/TodoForm';
import TodoList from './Todo/TodoList';
import Bucket from './Todo/Bucket';


function App() {
  const [state,setState]=useState(
    {bucket:[
      {name: '',
      items:[]
      }
    ],
    filter:['all'],
    selectedBucket:0
  });

  function addItemToState(workItem){
    let item = {item:workItem,isComplete:false,isChecked:false,isEditable:false};
    let newState = JSON.parse(JSON.stringify(state));
    newState.bucket[selectedBucket].items.push(item);
    setState(newState);
  }

  function addItemToBucket(bucketList){
    let bucket = {name:bucketList,items:[]};
    let newState = JSON.parse(JSON.stringify(state));
    newState.bucket.push(bucket);
    setState(newState);
  }

  function removeItemFromList(index){
    let newState = JSON.parse(JSON.stringify(state));
    newState.bucket[selectedBucket].items.splice(index, 1); 
    setState(newState);
  }

  function changeEditModeinList(index){
    let newState = JSON.parse(JSON.stringify(state));
    newState.bucket[selectedBucket].items[index].isEditable = !newState.bucket[selectedBucket].items[index].isEditable;
    setState(newState);
  }

  function changeIsDone(check, index){ 
    let newState = JSON.parse(JSON.stringify(state));
    newState.bucket[selectedBucket].items[index].isComplete = check;
    newState.bucket[selectedBucket].items[index].isChecked=!newState.bucket[selectedBucket].items[index].isChecked;
    setState(newState);
  }

  function updateIteminState(item,index){
    let newState = JSON.parse(JSON.stringify(state));
    newState.bucket[selectedBucket].items[index].item = item;
    newState.bucket[selectedBucket].items[index].isEditable=false;
    setState(newState);
  }

  function updateListonResults(value){
    let newState = JSON.parse(JSON.stringify(state));
    newState.filter[0] = value;
    setState(newState);
  }



  return (
    <div className="App">
       <Header/>
      <div className='row'>
        <div className='col-sm-5'>
          <Bucket addtoBucket={addItemToBucket}/>
        </div>
        <div className='col-sm-7'>
      <TodoForm filter={state.filter}
                onAdd={addItemToState}
                passResults={updateListonResults}/>
      <TodoList list={state.bucket[selectedBucket].items}
                filter={state.filter[0]} 
                removeItem={removeItemFromList} 
                changeDoneStatus ={changeIsDone}
                changeEditMode={changeEditModeinList} 
                updateIteminState={updateIteminState} />
                </div>
    </div>
    </div>
  );
}

export default App;
