import React, { useState } from 'react';
import './App.css';
import Header from './Header';
import TodoForm from './Todo/TodoForm';
import TodoList from './Todo/TodoList';


function App() {
  const [state,setState]=useState(
    {list:
      {items:[]
      }
    });

  function addItemToState(workItem){

    let item = {item:workItem,isComplete:false,isChecked:false,isEditable:false};
    let newState = JSON.parse(JSON.stringify(state));
    newState.list.items.push(item);
    setState(newState);
  }

  function removeItemFromList(index){
    let newState = JSON.parse(JSON.stringify(state));
    newState.list.items.splice(index, 1); 
    setState(newState);
  }

  function changeEditModeinList(index){
    let newState = JSON.parse(JSON.stringify(state));
    newState.list.items[index].isEditable = !newState.items[index].isEditable;
    setState(newState);
  }

  function changeIsDone(check, index){ 
    let newState = JSON.parse(JSON.stringify(state));
    newState.list.items[index].isComplete = check;
    newState.list.items[index].isChecked=true;
    setState(newState);

  }

  function updateIteminState(item,index){
    let newState = JSON.parse(JSON.stringify(state));
    newState.list.items[index].item = item;
    newState.list.items[index].isEditable=false;
    setState(newState);
  }

  return (
    <div className="App">
      <Header/>
      <TodoForm onAdd={addItemToState}/>
      <TodoList list={state.list.items} 
       removeItem={removeItemFromList} 
        changeDoneStatus ={changeIsDone}
        changeEditMode={changeEditModeinList} 
        updateIteminState={updateIteminState} />
    </div>
  );
}

export default App;
