import React,{useState} from 'react';

function TodoList(props) {
    const [input,changeInput]=useState('');

    function handleChange(e){
        changeInput(e.target.value);
    }

    function removeItem(i){
      props.removeItem(i);
    }
   /*  function editItem(index,item){
        console.log(item);
     props.editItem(index,item);
    } */

    function changeEditMode(i){
        props.changeEditMode(i);
    }

  return (
    <div>
        <ul>
             {Object.keys(props.list).map((item,index) => {
                    return (
                        <li key={index} > 
                                <div>
                                {
                                props.list[index].isEditable ?
                                <input defaultValue={props.list[index].item} onChange={handleChange}></input>:
                                <div style={{float:'left'}} onClick={()=>changeEditMode(index)}>{props.list[index].item}</div>
                                }
                                </div>
                                <button type='button' className="btn btn-danger " onClick={()=>removeItem(index)}>Delete</button>
                        </li>  
                )})}
        </ul>
    </div>
  );
}

export default TodoList;
