import React,{useState} from 'react';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
import './../list.css';

function TodoList(props) {
    var markDone;
    const [input,changeInput]=useState();

    function changeEditMode(i,item){
        changeInput(item);
        props.changeEditMode(i);
    }

    function removeItem(i){
        props.removeItem(i);
    }

    function handleChange(e,i){
        var target= e.target;
        target.name === 'isComplete'? props.changeDoneStatus(target.checked,i) : changeInput(target.value);
    }

    function updateIteminState(i){
        props.updateIteminState(input,i);
    }
    var newList = props.list;
    var status = props.filter;
    switch (status){
        case 'false':
         newList = newList.filter(list => !list.isComplete)
         break;
         case 'true':
         newList = newList.filter(list => list.isComplete)
         break;
         default:
         break;    
    };


  return (
    <div>
        <ul className='list-group'>
          {newList.map((item,index) => {
              // eslint-disable-next-line
                    {markDone = item.isComplete ? 'line-through':'none'}
                    return (
                        <li className='list-group-item todo-list-item' key={index}> 
                                <div>
                                {
                                item.isEditable ?
                                <div>
                                    <input 
                                        style={{float:'left'}} 
                                        value={input} 
                                        onChange={(e)=>handleChange(e,index)}>
                                    </input>
                                    <Button variant="outlined"  style={{float:'left'}} onClick={()=>changeEditMode(index)}>X</Button>
                                    <Button variant="outlined"  style={{float:'left'}} onClick={()=>updateIteminState(index)}>OK</Button>
                                </div>:
                                <div>  
                                    <div className='container'> 
                                    <input id='todo-list' style={{float:'left'}} name="isComplete" type="checkbox" onChange={(e)=> handleChange(e,index)} checked={item.isChecked}></input>
                                    <label htmlFor='todo-list' style={{float:'left',textDecoration:markDone}} >{item.item}</label>
                                    <IconButton aria-label="delete"  onClick={()=>removeItem(index)}>
                                    <DeleteIcon fontSize="large" />
                                        </IconButton>
                                    <button type='button' className="btn btn-danger " >Delete</button>
                                    <button style={{marginLeft:'5px'}}type='button' className="btn btn-secondary" onClick={()=>changeEditMode(index,item.item)}>Edit</button>
                                </div>
                                </div>
                                }
                                </div>
                        </li>  
                            )})}  
        </ul>
    </div>
  );
} 

export default TodoList;
