import React, { useState } from 'react';
import './App.css';
import Header from './Header';
import TodoForm from './Todo/TodoForm';
import TodoList from './Todo/TodoList';


function App() {
  const [state,setState]=useState({items:[]});

  function addItemToState(item){
    setState(state => ({
      ...state, items:[
      ...state.items, item
    ]}));
    console.log(state);
  }

  function removeItemFromList(index,e){
    let newState = Object.assign({},state);
    newState.items.splice(index, 1); 
    setState(newState);
  }

  return (
    <div className="App">
      <Header/>
      <TodoForm onAdd={addItemToState}/>
      <TodoList list={state.items} removeItem={removeItemFromList}/>
    </div>
  );
}

export default App;
