import React,{useState} from 'react';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';

import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';
import './../list.css';

const useStyles = makeStyles((theme) => ({
    root: {
      width: '100%',
      backgroundColor: theme.palette.background.paper,
    },
  }));

function TodoList(props) {
    var markDone;
    const classes = useStyles();
    const [input,changeInput]=useState();

    function changeEditMode(i,item){
        changeInput(item);
        props.changeEditMode(i);
    }

    function removeItem(i){
        props.removeItem(i);
    }

    function handleChange(e,i){
        var target= e.target;
        target.name === 'isComplete'? props.changeDoneStatus(target.checked,i) : changeInput(target.value);
    }

    function updateIteminState(i){
        props.updateIteminState(input,i);
    }
    var newList = props.list;
    var status = props.filter;
    switch (status){
        case 'false':
         newList = newList.filter(list => !list.isComplete)
         break;
         case 'true':
         newList = newList.filter(list => list.isComplete)
         break;
         default:
         break;    
    };


  return (
    <div>
        <List dense className={classes.root}>
          {newList.map((item,index) => {
              // eslint-disable-next-line
                    {markDone = item.isComplete ? 'line-through':'none'}
                    return (
                        <ListItem button className='todo-list-item' key={index}> 
                                <div>
                                {
                                item.isEditable ?
                                <div>
                                    <input 
                                        style={{float:'left'}} 
                                        value={input} 
                                        onChange={(e)=>handleChange(e,index)}>
                                    </input>
                                    <Button variant="outlined"  style={{float:'left'}} onClick={()=>changeEditMode(index)}>X</Button>
                                    <Button variant="outlined"  style={{float:'left'}} onClick={()=>updateIteminState(index)}>OK</Button>
                                </div>:
                                <div style={{display:'inline-block'}}>  
                                    <Checkbox
                                    id='todo-list' 
                                    name="isComplete" 
                                    onChange={(e)=> handleChange(e,index)} 
                                    checked={item.isChecked}
                                    iconStyle={{fill: 'grey'}}></Checkbox>
                                    <ListItemText style={{paddingTop:'6px',textDecoration:markDone}} >{item.item}</ListItemText>
                                    <IconButton style={{flex: 1}}aria-label="delete" edge='end' onClick={()=>removeItem(index)}>
                                    <DeleteIcon fontSize="medium" />
                                    </IconButton>
                                    <Button edge='end' variant="outlined"style={{marginLeft:'5px'}} onClick={()=>changeEditMode(index,item.item)}>Rename</Button>
                                </div>
                                }
                                </div>
                        </ListItem>  
                            )})}  
        </List>
    </div>
  );
} 

export default TodoList;
