import React, { useState } from 'react';
import './App.css';
import Header from './Header';
import TodoForm from './Todo/TodoForm';
import TodoList from './Todo/TodoList';


function App() {
  const [state,setState]=useState([]);

  function addItemToState(workItem){
    var item = {item:workItem,isComplete:false,isEditable:false};
    console.log(state);
    setState(
     [ ...state,item]); 
    console.log(state);
  }

  function removeItemFromList(index){
    let newState = JSON.parse(JSON.stringify(state));
    newState.splice(index, 1); 
    setState(newState);
  }

  function changeEditModeinList(index){
    let newState = JSON.parse(JSON.stringify(state));
    newState[index].isEditable = !state[index].isEditable;
    setState(newState);
  }

  function changeIsDone(check, index){
    let newState = JSON.parse(JSON.stringify(state));
    newState[index].isDone = !check;
    setState(newState);

  }

  function updateIteminState(item,index){
    let newState = JSON.parse(JSON.stringify(state));
    newState[index].item = item;
    newState[index].isEditable=false;
    setState(newState);
  }

  return (
    <div className="App">
      <Header/>
      <TodoForm onAdd={addItemToState}/>
      <TodoList list={state} 
        removeItem={removeItemFromList} 
        changeDoneStatus ={changeIsDone}
        changeEditMode={changeEditModeinList} 
        updateIteminState={updateIteminState}/>
    </div>
  );
}

export default App;
