import React from 'react';
import {Link} from '@material-ui/core';
import {makeStyles} from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    input:{
        color:'black',
    }    
    }));

function BucketList(props) {
    const classes=useStyles();
    var bucketId = parseInt(props.bucketId);
    var bucketitems =props.bucket;

    function chooseBucket(e){
        props.chooseBucket(e.currentTarget.dataset.id);
    }

    return(
        <div className='row'>
            <div className='container'>
        {
            bucketitems.map((item,i)=>{ 
            var name ='';
            (i === bucketId )? name = 'list-group-item active': name='list-group-item';

            return(
                // eslint-disable-next-line
            <a href='#' key={i} data-id={i} value={i} className={name} onClick={chooseBucket} >
                <span className="badge badge-primary badge-pill" >{item.items.length}</span>
            {item.name}
            </a>
            )
        }
            )}
            </div>
            </div>
    )
}

export default BucketList;
