import React, { useState,useRef } from 'react';

function BucketList(props) {
    var bucketId = props.bucketId;
    var bucketitems =props.bucket;

    function changeBucket(e){
        props.chooseBucket(e.target.dataset.id);
    }

    return(
        <div className='list-group list-group-flush'>
            <div className='sidebar-form-list'>
        {
            bucketitems.map((item,i)=>{ 
            var name ='';
            (i == bucketId )? name = 'list-group-item active': name='list-group-item';

            return(
            <a href='#' key={i} data-id={i} value={i} className={name} onClick={changeBucket} >
                <span className="badge badge-primary badge-pill" >{item.items.length}</span>

            {item.name}
            </a>
            )
        }
            )}
            </div>
            </div>
    )
}

export default BucketList;
