import React, { useState } from 'react';
import './App.css';
import Header from './Header';
import TodoForm from './Todo/TodoForm';
//import TodoList from './Todo/TodoList';


function App() {
  const [state,setState]=useState({items:[]});

  function addItemToState(todo){
    setState(state.items.push([todo]));

  }

  return (
    <div className="App">
      <Header/>
      <TodoForm onAdd={(todo)=> addItemToState(todo)}/>
    </div>
  );
}

export default App;
