import React, { useState } from 'react';
import './App.css';
import Header from './Header';
import SideBar from './Todo/SidebarForm';
import TodoForm from './Todo/TodoForm';
import TodoList from './Todo/TodoList';
import SidebarForm from './Todo/SidebarForm';


function App() {
  const [state,setState]=useState(
    {list:
      {items:[]
      },
      filter:['all']
    });

  function addItemToState(workItem){
    let item = {item:workItem,isComplete:false,isChecked:false,isEditable:false};
    let newState = JSON.parse(JSON.stringify(state));
    newState.list.items.push(item);
    setState(newState);
  }

  function removeItemFromList(index){
    let newState = JSON.parse(JSON.stringify(state));
    newState.list.items.splice(index, 1); 
    setState(newState);
  }

  function changeEditModeinList(index){
    let newState = JSON.parse(JSON.stringify(state));
    newState.list.items[index].isEditable = !newState.list.items[index].isEditable;
    setState(newState);
  }

  function changeIsDone(check, index){ 
    let newState = JSON.parse(JSON.stringify(state));
    newState.list.items[index].isComplete = check;
    newState.list.items[index].isChecked=!newState.list.items[index].isChecked;
    setState(newState);
  }

  function updateIteminState(item,index){
    let newState = JSON.parse(JSON.stringify(state));
    newState.list.items[index].item = item;
    newState.list.items[index].isEditable=false;
    setState(newState);
  }

  function updateListonResults(value){
    let newState = JSON.parse(JSON.stringify(state));
    newState.filter[0] = value;
    setState(newState);
  }



  return (
    <div className="App">
       <Header/>
      <div className='row'>
        <div className='col-sm-5'>
          <SidebarForm/>
        </div>
        <div className='col-sm-7'>
      <TodoForm filter={state.filter}
                onAdd={addItemToState}
                passResults={updateListonResults}/>
      <TodoList list={state.list.items}
                filter={state.filter[0]} 
                removeItem={removeItemFromList} 
                changeDoneStatus ={changeIsDone}
                changeEditMode={changeEditModeinList} 
                updateIteminState={updateIteminState} />
                </div>
    </div>
    </div>
  );
}

export default App;
