import React, { useState,useRef } from 'react';
import {TextField, Fab} from '@material-ui/core';
import {makeStyles} from '@material-ui/core/styles';
import AddIcon from '@material-ui/icons/Add';

const useStyles = makeStyles((theme) => ({
    input:{
        margin: theme.spacing(2),
        height: 40,
}}));

function Bucket(props) {
    const classes = useStyles();
    const [bucketList,setbucketlist]=useState('');
    const bucketInput = useRef();

    function handleChange(e){
       setbucketlist(e.target.value);
    }

    function addToBucket(e){
        e.preventDefault();
        if(bucketInput.current.value.trim()!==''){
            props.addtoBucket(bucketList);
            setbucketlist('');
            bucketInput.current.focus();
        }
        else{
            bucketInput.current.focus();
        } 
    }


  return (
    <div>
         <div className="row">
                <div className='form-group sidebar-form'>
                    <p>Buckets</p>
                    <div className='container'>
                    <form onSubmit={addToBucket}> 
                    <TextField InputProps={{className: classes.input}} 
                                InputLabelProps={{shrink: true}}
                                variant='filled' 
                                color='secondary'
                                label='Add task in bucket'
                                inputRef={bucketInput} value={bucketList} 
                                onChange={handleChange}/>
                    <Fab color="secondary" aria-label="add" className={classes.input} type='submit'>
                     <AddIcon />
                    </Fab>                   
                </form>
                    </div>

                </div>
        </div>
    </div>
  );
}

export default Bucket;
