import React, { useState } from 'react';
import './App.css';
import Header from './Header';
import TodoForm from './Todo/TodoForm';
import TodoList from './Todo/TodoList';


function App() {
  const [state,setState]=useState({items:[{item:'Laundary',complete:false},]});

  function addItemToState(workItem){
    // setState(state => ({
    //   ...state, items:[
    //   ...state.items, item
    // ]}));
    var item = {item:workItem,complete:false};
    var allItems = state.items.concat([item]);
    setState(allItems);
    console.log(state);
  }

  function removeItemFromList(index){
    console.log(index);
    let newState = Object.assign({},state);
    newState.items.splice(index, 1); 
    setState(newState);
  }

  function editIteminList(index,val){
    let updatedItem = Object.assign({},state.items[index]);
    updatedItem = val;
    let newItem = Object.assign([],state.items);
    newItem[index]=updatedItem;
    setState(
    state => ({
      ...state.items,newItem
  }));
}

  return (
    <div className="App">
      <Header/>
      <TodoForm onAdd={addItemToState}/>
      <TodoList list={state.items} editItem={editIteminList} removeItem={removeItemFromList}/>
    </div>
  );
}

export default App;
