import React,{useState} from 'react';

function TodoList(props) {
    const [input,changeInput]=useState();

    function changeEditMode(i,item){
        changeInput(item);
        props.changeEditMode(i);
    }

    function removeItem(i){
        props.removeItem(i);
    }

    function handleChange(e,i){
        const target= e.target;
        target.name === 'isComplete'? props.changeDoneStatus(target.checked,i) : changeInput(e.target.value);
    }

    function updateIteminState(i){
        props.updateIteminState(input,i);
    }

  return (
    <div>
        <ul>
             {Object.keys(props.list).map((item,index) => {
                    return (
                        <li key={index} > 
                                <div>
                                {
                                props.list[index].isEditable ?
                                <div>
                                    <input 
                                        style={{float:'left'}} 
                                        value={input} 
                                        onChange={handleChange} 
                                        >
                                        </input>
                                    <button style={{float:'left'}} onClick={()=>changeEditMode(index)}>X</button>
                                    <button style={{float:'left'}} onClick={()=>updateIteminState(index)}>OK</button>
                                </div>:
                                <div>
                                    <input name='isComplete' type="checkbox" onChange={handleChange.bind(index)} defaultChecked={props.list[index].isComplete}></input>
                                    <div style={{float:'left'}} onClick={()=>changeEditMode(index,props.list[index].item)}>{props.list[index].item}</div>
                                    <button type='button' className="btn btn-danger " onClick={()=>removeItem(index)}>Delete</button>
                                </div>
                                }
                                </div>
                        </li>  
                )})}
        </ul>
    </div>
  );
}

export default TodoList;
