import React,{useState} from 'react';
import DeleteIcon from '@material-ui/icons/Delete';
import { makeStyles } from '@material-ui/core/styles';
import {List,ListItem,Chip,ListItemSecondaryAction,IconButton,Button} from '@material-ui/core';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import TextField from '@material-ui/core/TextField';
import Checkbox from '@material-ui/core/Checkbox';
import './../list.css';

const useStyles = makeStyles((theme) => ({
    root: {
      width: '100%',
      backgroundColor: theme.palette.background.paper,
    },
  }));

function TodoList(props) {
    var markDone;
    const classes = useStyles();
    const [input,changeInput]=useState();

    function changeEditMode(i,item){
        changeInput(item);
        props.changeEditMode(i);
    }

    function removeItem(i){
        props.removeItem(i);
    }

    function handleChange(e,i){
        e.preventDefault();
        var target= e.target;
        target.name === 'isComplete'? props.changeDoneStatus(target.checked,i) : changeInput(target.value);
    }

    function updateIteminState(i){
        props.updateIteminState(input,i);
    }
    var newList = props.list;
    var status = props.filter;
    switch (status){
        case 'false':
         newList = newList.filter(list => !list.isComplete)
         break;
         case 'true':
         newList = newList.filter(list => list.isComplete)
         break;
         default:
         break;    
    };


  return (
    <div>
        <List dense className={classes.root}>
          {newList.map((item,index) => {
              // eslint-disable-next-line
                    {markDone = item.isComplete ? 'line-through':'none'}
                    return (
                        <ListItem button className='todo-list-item' key={index}> 
                                <div>
                                {
                                item.isEditable ?
                                <div>
                                    <TextField style={{float:'left'}} 
                                        value={input} 
                                        onChange={(e)=>handleChange(e,index)}
                                        color='secondary'/>
                                    <ListItemSecondaryAction>
                                    <Button variant='outlined' onClick={()=>changeEditMode(index)} >X</Button>
                                    <Button color="secondary" variant="outlined"  style={{marginLeft:'5px'}} onClick={()=>updateIteminState(index)}>OK</Button>
                                    </ListItemSecondaryAction>    

                                </div>:
                                <div> 
                                    <FormControlLabel
                                    control={
                                        <Checkbox
                                    value={item.item}
                                    id='todo-list' 
                                    name="isComplete" 
                                    onChange={(e)=> handleChange(e,index)} 
                                    checked={item.isChecked}
                                    iconStyle={{fill: 'grey'}}/>}
                                    label={item.item} style={{textDecoration:markDone}}/> 
                                    <ListItemSecondaryAction>
                                    <IconButton aria-label="delete" onClick={()=>removeItem(index)}>
                                    <DeleteIcon fontSize="medium" />
                                    </IconButton>
                                    <Chip label={'Rename'} style={{marginLeft:'5px'}} onClick={()=>changeEditMode(i,item.item)}/>
                                    </ListItemSecondaryAction>

                                </div>
                                }
                                </div>
                        </ListItem>  
                            )})}  
        </List>
    </div>
  );
} 

export default TodoList;
