import React,{useState} from 'react';
import './../list.css';
import {Divider,Chip,ListItemSecondaryAction,Button,TextField,ListItem,List} from '@material-ui/core';
import {makeStyles} from '@material-ui/core/styles';


const useStyles = makeStyles(theme => ({
    root: {
      backgroundColor:'grey',
    },
    }));


function BucketList(props) {
    const classes=useStyles();
    var bucketId = parseInt(props.bucketId);

    function chooseBucket(e){
        props.chooseBucket(e.currentTarget.dataset.id);
    }

    return(
        <div className='row'>
            <div className='container sidebar-form-list'>
                <div className='sidebar-form-list'>                     
        {
            props.bucket.map((item,i) => { 
            return(
                <div>
            <div>
            {
                item.isRename ?
                    <div>
                            <TextField>heyo</TextField>
                            {
                            /* <TextField style={{float:'left'}} 
                                        value={bucketName} 
                                        onChange={(e)=>handleChange(e,i)}
                                        color='secondary'/>
                            <ListItemSecondaryAction>
                                    <Button variant='outlined' onClick={()=>changeEditMode(i)} >X</Button>
                                    <Button color="secondary" variant="outlined"  onClick={()=>updateBucketinState(i)}>OK</Button>
                            </ListItemSecondaryAction>  */ }
                                </div> :

                                <div>
                                    <a href='#' key={i} data-id={i} value={i} className={(i === bucketId )? 'list-group-item active' : 'list-group-item'} onClick={chooseBucket} >
                                    <span>{item.name} </span>
                                    <ListItemSecondaryAction>
                                        <Chip edge='end' onClick={()=>changeEditMode(i,item)} label='X'></Chip>        
                                        <Chip color='secondary' edge='end'  label={item.items.length}></Chip>
                            </ListItemSecondaryAction>
                            </a>
                                </div>}

                                 </div> 

            <Divider variant='middle' className={classes.root} />
            </div>
            )
        }
            )}
            </div>
            </div>
            </div>
    )
}

export default BucketList;
