import React, { useState } from 'react';
import './App.css';
import Header from './Header';
import TodoForm from './Todo/TodoForm';
import TodoList from './Todo/TodoList';


function App() {
  const [state,setState]=useState([]);

  function addItemToState(item){
    console.log(`In app.js: state.item ${state}`);
    console.log(`In app.js: todo item ${item}`);
    setState(state => [...state, item]);

  }

  return (
    <div className="App">
      <Header/>
      <TodoForm onAdd={addItemToState}/>
      <TodoList list={state}/>
    </div>
  );
}

export default App;
