import React, { useState,useRef } from 'react';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import { Paper, Tab,Tabs ,Button} from '@material-ui/core';
import './../list.css';

const useStyles = makeStyles((theme) => ({
    root: {
       flexGrow:1
    },
    input:{
        height: 40
    }
  }));


function TodoForm(props) {
    const classes = useStyles();
    const [todo,settodo]=useState('');
    const textInput = useRef();


    function handleChange(e,newValue){       
        e.target.name==='filterResult' ? props.passResults(newValue): settodo(e.target.value);
        //settodo(e.target.value);
    }

    function handlethisChange(e,newValue){
        console.log(newValue);
        props.passResults(newValue)
    }

    function addToList(e){
        e.preventDefault();
        if(textInput.current.value.trim()!==''){
            props.onAdd(todo);
            settodo('');
            textInput.current.focus();
        }
        else{
            textInput.current.focus();
        }
    }

  return (
    <div>
         <div className='row'>
            <div className='col-md-6'>
                <form onSubmit={addToList}> 
                    <TextField InputProps={{className: classes.input}} 
                                InputLabelProps={{shrink: true}}
                                variant='outlined' 
                                color='secondary'
                                label='Add task in bucket'
                                inputRef={textInput} 
                                value={todo} 
                                onChange={(e)=>handleChange(e)}/>
                    <Button variant='outlined' 
                            style={{marginLeft:'5px'}}
                            className={classes.input} 
                            type='submit'
                            color='secondary'>Add</Button>        
                </form>
            </div>
            <div className='col-md-6'>
            <Paper className={classes.root}>
                    <Tabs name='filterTesult'
                          indicatorColor="secondary"
                          textColor="secondary"
                          centered
                          value={props.filter}
                          onChange={handleChange}>
                    <Tab name='filterResult' value='all' label="All" />
                    <Tab name='filterResult' value='false' label="Pending" />
                    <Tab name='filterResult' value='true' label="Done" />
                    </Tabs>
            </Paper>
            </div>
        </div>
    </div>
  );
}

export default TodoForm;
