import React from 'react';

function TodoList(props) {

    function removeItem(i){
      props.removeItem(i);
    }
    function editItem(index,item){
        console.log(item);
     props.editItem(index,item);
    }

  return (
    <div>
        <ul>
             {props.list.map((item,index) => {
                    return (
                        <li key={index} value={index} style={{float:'left'}}> 
                                <span>{item}</span>
                                <button type='button' value={item} className="btn btn-secondary pull-right" onClick={() => editItem(index,item)}>Edit</button>
                                <button type='button' className="btn btn-danger pull-right" onClick={()=>removeItem(index)}>Delete</button>
                        </li>  
                )})}
        </ul>
    </div>
  );
}

export default TodoList;
