    var item = {item:workItem,isComplete:false,isEditable:false};
    console.log(state);
    setState(
     [ ...state,item]); 
    console.log(state);
  }

  function removeItemFromList(index){
    console.log(index);
    let newState = JSON.parse(JSON.stringify(state))
    newState.splice(index, 1); 
    setState(newState);
  }

  function changeEditMode(index){
    console.log(index);
    let newState = JSON.parse(JSON.stringify(state))
