import React,{useState} from 'react';
import './../list.css';
import {Divider,Chip,ListItemSecondaryAction,Button,TextField} from '@material-ui/core';
import {makeStyles} from '@material-ui/core/styles';


const useStyles = makeStyles(theme => ({
    root: {
      backgroundColor:'grey',
    },
    }));


function BucketList(props) {
    const classes=useStyles();
    const [bucket,changeBucket]=useState();
    var bucketId = parseInt(props.bucketId);
    var bucketitems =props.bucket;

    function chooseBucket(e){
        props.chooseBucket(e.currentTarget.dataset.id);
    }

    function changeEditMode(i,item){
        changeBucket(item);
        props.changeEditMode(i);
    }

    function handleChange(e){
        e.preventDefault();
        changeBucket(e.target.value);
    }

    function updateBucketinState(i){
        props.updateBucketinState(bucket,i);
    }

    return(
        <div className='row'>
            <div className='container sidebar-form-list'>
                <div className='sidebar-form-list'>

        {
            bucketitems.map((item,i)=>{ 
            var name ='';
            (i === bucketId )? name = 'list-group-item active' : name='list-group-item';

            return(
                // eslint-disable-next-line
            <>  
            <div>
            {
                 item.edit ?
                                <div>
                                    <TextField style={{float:'left'}} 
                                        value={bucket} 
                                        onChange={(e)=>handleChange(e,i)}
                                        color='secondary'/>
                                    <ListItemSecondaryAction>
                                    <Button variant='outlined' onClick={()=>changeEditMode(i)} >X</Button>
                                    <Button color="secondary" variant="outlined"  onClick={()=>updateBucketinState(i)}>OK</Button>
                                    </ListItemSecondaryAction>    
                                </div>:
                                <div>
                                <a href='#' key={i} data-id={i} value={i} className={name} onClick={chooseBucket} >
                                <span>{item.name} </span>
                                <ListItemSecondaryAction>
                            <Chip edge='end' onClick={()=>changeEditMode(i,item.name)} label='X'></Chip>        
                            <Chip color='secondary' edge='end' className='chip' label={item.items.length}></Chip>
                            </ListItemSecondaryAction>
                            </a>
            </div> }

                </div> 


            <Divider variant='middle' className={classes.root} />
            </>
            )
        }
            )}
            </div>
            </div>
            </div>
    )
}

export default BucketList;
