import React, { useState,useRef } from 'react';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import { List, ListItem ,Button} from '@material-ui/core';
import './../list.css';



const useStyles = makeStyles((theme) => ({
    root: {
       flexGrow:1
    },
    input:{
        height: 40
    }
  }));


function TodoForm(props) {
    const classes = useStyles();
    const [todo,settodo]=useState('');
    const textInput = useRef();


    function handleChange(e){
        e.target.name==='filterResult' ? props.passResults(e.target.value): settodo(e.target.value);
    }

    function addToList(e){
        e.preventDefault();
        if(textInput.current.value.trim()!==''){
            props.onAdd(todo);
            settodo('');
            textInput.current.focus();
        }
        else{
            textInput.current.focus();
        }
    }

    function showResults(value){
        return (props.filter[0]===value ?'btn-primary':'btn-deafult');
    }

  return (
    <div>
         <div className='row'>
            <div className='col-md-6'>
                <form onSubmit={addToList}> 
                    <TextField InputProps={{className: classes.root}} 
                                InputLabelProps={{shrink: true}}
                                variant='outlined' 
                                color='secondary'
                                label='Add task in bucket'
                                inputRef={textInput} 
                                value={todo} 
                                onChange={(e)=>handleChange(e)}/>
                    <Button variant='outlined' 
                            style={{marginLeft:'5px'}}
                            className={classes.root} 
                            type='submit'
                            color='secondary'>Add</Button>        
                </form>
            </div>
            <div className='col-md-6 list-horizontal-display '>
            <Paper className={classes.root}>
      <Tabs
        value={value}
        onChange={handleChange}
        indicatorColor="primary"
        textColor="primary"
        centered
      >
        <Tab label="Item One" />
        <Tab label="Item Two" />
        <Tab label="Item Three" />
      </Tabs>
    </Paper>

                <List>              
                    <ListItem><button name='filterResult' onClick={handleChange} className={`btn ${showResults('all')}`} value='all'>All</button></ListItem>
                    <ListItem><button name='filterResult' onClick={handleChange} className={`btn ${showResults('false')}`} value="false">Pending</button></ListItem>
                    <ListItem><button name='filterResult' onClick={handleChange} className={`btn ${showResults('true')}`}value="true">Done</button></ListItem>
                </List>
            </div>
        </div>
    </div>
  );
}

export default TodoForm;
